mapper.select:
select id as id ,dept_cd as deptCd ,dept_nm as deptNm ,instt_cd as insttCd ,instt_nm as insttNm ,DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt ,DATE_FORMAT(updated_at, '%Y-%m-%d') as updatedAt from T_DEPT where 1=1
AND (instt_nm like CONCAT('%',#{keyword},'%') OR dept_nm like CONCAT('%',#{keyword},'%'))
limit #{srchCnt} offset #{offset}

select id as id ,auth_nm as authNm ,auth_typ as authTyp ,(SELECT cd_nm FROM T_CMMN_CD WHERE cd_typ = 'AuthTyp' AND cd = auth_typ) as authTypNm ,dept_auth_yn as deptAuthYn ,dept_cd as deptCd ,(SELECT COUNT(*) FROM T_EMP_AUTH WHERE auth_id = id) as authEmpCnt ,use_yn as useYn ,DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt ,DATE_FORMAT(update_at, '%Y-%m-%d') as updatedAt from T_AUTH where 1=1
AND auth_typ = #{srchCond}
AND auth_nm like CONCAT('%',#{keyword},'%')
limit #{srchCnt} offset #{offset}

select count(*) from T_AUTH where 1=1
AND auth_typ = #{srchCond}
AND auth_nm like CONCAT('%',#{keyword},'%')
