mapper.select:
SELECT id as id ,emp_nm as empNm ,emp_cd as empCd ,dept_cd as deptCd ,dept_nm as deptNm ,instt_cd as insttCd ,instt_nm as insttNm ,belong_nm as belongNm ,''as userTypNm ,DATE_FORMAT(expired_dt, '%Y-%m-%d') as expiredDt ,DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt ,DATE_FORMAT(updated_at, '%Y-%m-%d') as updatedAt FROM T_EMP WHERE delete_yn = 'N'
AND expired_dt BETWEEN #{fromDt} AND #{toDt}
AND (instt_nm LIKE CONCAT('%',#{keyword1},'%') OR dept_nm LIKE CONCAT('%',#{keyword1},'%'))
AND (emp_cd = #{keyword2} OR emp_nm LIKE CONCAT('%',#{keyword2},'%'))
limit #{srchCnt} offset #{offset}

mapper.update:
UPDATE T_EMP SET delete_yn = 'Y', deleted_at = NOW(3) WHERE expired_dt between #{fromDt} and #{toDt}
AND (instt_nm like CONCAT('%',#{keyword1},'%') OR dept_nm like CONCAT('%',#{keyword1},'%'))
AND (emp_cd = #{keyword2} OR emp_nm like CONCAT('%',#{keyword2},'%'))

mapper.select:
select eh.totDate, CASE WHEN eh.card_tag_typ = "" THEN '얼굴인증' WHEN eh.card_tag_typ = "01" THEN '마이페어 카드' WHEN eh.card_tag_typ = "02" THEN '공무원증' WHEN eh.card_tag_typ = "03" THEN 'Mobile NFC' ELSE 'BLE' END as cardTagTyp , eh.cnt from (select card_tag_typ , totDate, sum(cnt) as cnt FROM (select DATE_FORMAT(created_at,'%Y-%m-%d') as totDate, card_tag_typ, building_cd, count(1) as cnt FROM T_ENT_HIST group by DATE_FORMAT(created_at,'%Y-%m-%d'), card_tag_typ, building_cd having card_tag_typ in ("01", "02", "03", "04", "")) a group by building_cd, card_tag_typ, totDate having 1=1
AND totDate BETWEEN #{fromDt} AND #{toDt}
) eh
AND building_cd = #{srchCond3}
AND card_tag_typ = #{srchCond1}
order by totDate desc limit #{srchCnt} offset #{offset}

select count(*) from
(select  card_tag_typ , totDate, sum(cnt) as cnt FROM
(select DATE_FORMAT(created_at,'%Y-%m-%d') as totDate, card_tag_typ, building_cd, count(1) as cnt
 FROM T_ENT_HIST group by  DATE_FORMAT(created_at,'%Y-%m-%d'), card_tag_typ, building_cd
having card_tag_typ in ("01", "02", "03", "04", "")) a
group by building_cd, card_tag_typ, totDate
having 1=1

    AND totDate BETWEEN #{fromDt} AND #{toDt}

) eh

    AND building_cd = #{srchCond3}


    AND card_tag_typ = #{srchCond1}
