mapper.select:
SELECT TM.id as id, TB.building_nm as buildingNm, TF.floor_nm as floorNm, TD.door_nm as doorNm, TM.mgmt_num as mgmtNum, TM.terminal_cd as terminalCd, TM.terminal_typ as terminalTyp, (SELECT cd_nm FROM T_CMMN_CD WHERE cd_typ = 'TerminalTyp' AND cd = TM.terminal_typ) AS terminalTypNm, TM.ip_addr as ipAddr, TM.complex_auth_typ as complexAuthTyp, (SELECT cd_nm FROM T_CMMN_CD WHERE cd_typ = 'ComplexAuthTyp' AND cd = TM.complex_auth_typ) AS complexAuthTypNm, TM.face_auth_typ as faceAuthTyp, (SELECT cd_nm FROM T_CMMN_CD WHERE cd_typ = 'FaceAuthTyp' AND cd = TM.face_auth_typ) AS faceAuthTypNm, (SELECT COUNT(*) FROM T_WB_LIST WHERE terminal_id = TM.id AND wb_typ = 'BLACK') AS blackListCnt, (SELECT COUNT(*) FROM T_WB_LIST WHERE terminal_id = TM.id AND wb_typ = 'WHITE') AS whiteListCnt, DATE_FORMAT(TM.created_at, '%Y-%m-%d') AS createdAt, TM.use_yn as useYn FROM T_TERMINAL TM LEFT OUTER JOIN T_DOOR TD ON TM.door_id = TD.id JOIN T_BUILDING TB ON TD.building_id = TB.id LEFT OUTER JOIN T_FLOOR TF ON TD.floor_id = TF.id WHERE 1=1
AND TB.id = #{srchCond1}
AND TM.terminal_typ = #{srchCond2}
AND (TM.terminal_cd like CONCAT('%',#{keyword},'%') OR TM.mgmt_num like CONCAT('%',#{keyword},'%'))
limit #{srchCnt} offset #{offset}

select count(*) from T_TERMINAL TM LEFT OUTER JOIN T_DOOR TD ON TM.door_id = TD.id JOIN T_BUILDING TB ON TD.building_id = TB.id LEFT OUTER JOIN T_FLOOR TF ON TD.floor_id = TF.id WHERE 1=1
AND TB.id = #{srchCond1}
AND TM.terminal_typ = #{srchCond2}
AND (TM.terminal_cd like CONCAT('%',#{keyword},'%') OR TM.mgmt_num like CONCAT('%',#{keyword},'%'))

mapper.delete:
DELETE FROM T_WB_LIST
WHERE
    wb_typ = #{wbTyp}
    AND terminal_id = #{id}
