mapper.select:
SELECT id , building_cd , floor_cd , door_cd , building_id , area_id , floor_id , door_nm , alarm_typ FROM T_DOOR TD WHERE 1=1
AND TD.building_id = #{building_id}
AND TD.area_id = #{area_id}
AND TD.floor_id = #{floor_id}
ORDER BY TD.door_cd DESC

mapper.insert:
	INSERT
	  INTO
    T_DOOR ( building_cd
           , floor_cd
           , door_cd
           , building_id
    	   , area_id
    	   , floor_id
    	   , door_nm
    	   , alarm_typ
    	   , created_at
    	   , updated_at
	       )
	VALUES ( #{buildingCd}
		   , #{floorCd}
		   , #{doorCd}
		   , #{buildingId}
		   , #{areaId}
		   , #{floorId}
		   , #{doorNm}
		   , #{alarmGroupId}
		   , NOW(6)
		   , NOW(6)
		   )

mapper.update:
UPDATE T_DOOR
   SET building_cd = #{buildingCd}
     , floor_cd = #{floorCd}
     , door_cd = #{doorCd}
     , building_id = #{buildingId}
     , area_id = #{areaId}
     , floor_id = #{floorId}
     , door_nm = #{doorNm}
     , alarm_typ = #{alarmGroupId}
	 , sch_doorgrp_id = #{schDoorgrpId}
     , updated_at = NOW(6)
 WHERE id = #{id}

UPDATE T_DOOR
   SET sch_doorgrp_id = #{schDoorgrpId}
     , updated_at = NOW(6)
 WHERE id = #{id}

mapper.select:
SELECT TB.id , TB.building_cd , TB.workplace_id , TB.building_nm FROM T_BUILDING TB WHERE 1=1
AND TB.id = #{id}
AND TB.workplace_id = #{workplace_id}
ORDER BY TB.building_cd ASC

SELECT id , building_id , building_cd , area_id , floor_cd , floor_nm FROM T_FLOOR TF WHERE 1=1
AND TF.id = #{id}
AND TF.area_id = #{areaId} AND TF.building_id = #{buildingId}
ORDER BY building_cd ASC , FIELD(floor_cd,'B1','B2','B3','B4','B5') DESC , floor_cd ASC

SELECT id , building_id , area_nm FROM T_AREA TA WHERE 1=1
AND TA.id = #{id}
AND TA.building_id = #{buildingId}

mapper.update:
UPDATE T_TERMINAL
   SET door_id = #{doorId}
     , updated_at= NOW(6)
 WHERE id = #{id}

mapper.insert:
INSERT
  INTO T_AUTH_DOOR
     ( auth_id
     , door_id
     , created_at
     )
VALUES
     ( #{authId}
	 , #{doorId}
	 , NOW(6)
	  )

mapper.delete:
DELETE
  FROM T_AUTH_DOOR
 WHERE auth_id = #{authId}
   AND door_id = #{doorId}

