mapper.select:
SELECT A.id , A.nm , A.time , A.env_yn , A.delete_yn , (select COUNT(TDA.door_id) from T_DOOR_ALARM TDA where TDA.dooralram_grp_id = A.id) as door_cnt , DATE_FORMAT(A.created_at, '%Y-%m-%d') as created_at , DATE_FORMAT(A.updated_at, '%Y-%m-%d') as updated_at FROM( SELECT TDG.id , TDG.nm , TDG.time , TDG.env_yn , TDG.delete_yn , TDG.created_at , TDG.updated_at FROM T_DOORALARM_GRP TDG WHERE 1=1
AND TDG.id = #{id}
AND TDG.nm like CONCAT('%',#{keyword},'%')
) A ORDER BY A.created_at DESC
LIMIT #{srchCnt} OFFSET #{offset}

SELECT COUNT(A.id) FROM ( SELECT TDG.id FROM T_DOORALARM_GRP TDG WHERE 1=1
AND TDG.id = #{id}
AND TDG.nm like CONCAT('%',#{keyword},'%')
) A

mapper.insert:
INSERT
  INTO T_DOORALARM_GRP
     ( nm
     , time
     , env_yn
     , delete_yn
     , created_at
     , updated_at
     )
VALUES
     ( #{nm}
     , #{time}
     , #{envYn}
     , #{deleteYn}
     , NOW()
     , NOW()
     )

mapper.update:
UPDATE T_DOORALARM_GRP
   SET nm = #{nm}
     , time = #{time}
     , env_yn = #{envYn}
     , delete_yn = #{deleteYn}
     , updated_at= NOW()
 WHERE id = #{id}

mapper.insert:
INSERT
  INTO T_DOOR_ALARM
     ( door_id
     , dooralram_grp_id)
VALUES
     ( #{doorId}
     , #{dooralramGrpId}
     )

mapper.update:
UPDATE T_DOOR_ALARM
   SET dooralram_grp_id = #{dooralramGrpId}
 WHERE door_id = #{doorId}
