mapper.select:
SELECT A.id , A.nm , A.door_sch_id , A.door_sch_nm , DATE_FORMAT(A.created_at, '%Y-%m-%d') as created_at , DATE_FORMAT(A.updated_at, '%Y-%m-%d') as updated_at , A.door_cnt FROM ( SELECT TD.id , TD.nm , TD.door_sch_id , TDC.door_sch_nm , (select COUNT(TDD.door_id) FROM T_DOORGRP_DOOR TDD where TDD.doorgrp_id = TD.id )AS door_cnt , TD.created_at , TD.updated_at -- FROM T_DOORGRP TD FROM T_SCH_DOORGRP TD LEFT JOIN T_DOOR_SCH TDC on TD.door_sch_id = TDC.id WHERE 1=1
AND TD.id = #{id}
AND TD.nm like CONCAT('%',#{keyword},'%')
AND TD.door_sch_id = #{doorSchId}
) A ORDER BY A.created_at DESC
LIMIT #{srchCnt} OFFSET #{offset}

SELECT COUNT(*) FROM ( SELECT TD.id -- FROM T_DOORGRP TD FROM T_SCH_DOORGRP TD LEFT JOIN T_DOOR_SCH TDC on TD.door_sch_id = TDC.id WHERE 1=1
AND TD.id = #{id}
AND TD.nm like CONCAT('%',#{keyword},'%')
) A

mapper.insert:
INSERT
INTO T_DOORGRP
( nm
, door_sch_id
, created_at
, updated_at
   )
VALUES
    ( #{nm}
    , #{doorSchId}
    , NOW()
    , NOW()
    )

mapper.update:
UPDATE T_DOORGRP
   SET updated_at= NOW()

     , door_sch_id = #{doorSchId}


     , nm = #{nm}

 WHERE id = #{id}

mapper.insert:
INSERT
  INTO T_DOORGRP_DOOR
     ( door_id
